import { Component } from '@angular/core';
import { RouterOutlet } from '@angular/router';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [RouterOutlet],
  templateUrl: './app.component.html',
  styleUrl: './app.component.scss'
})
export class AppComponent {
  title = 'sbdsp-angular';
}

import { Component, Input } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatExpansionModule } from '@angular/material/expansion';

@Component({
    selector: 'qds-accordion',
    standalone: true,
    imports: [CommonModule, MatExpansionModule],
    template: `
        <mat-accordion
            [class]="customClasses"
            [multi]="!openSingleItem"
            class="ds-accordion__wrapper"
        >
            <mat-expansion-panel
                *ngFor="let item of accordionContent"
                [expanded]="defaultExpanded"
                class="ds-accordion"
            >
                <mat-expansion-panel-header class="ds-accordion__title">
                    <div class="ds-accordion__title-wrapper">
                        <span
                            *ngIf="item.icon"
                            class="ds-icon--{{ item.icon }}"
                        ></span>
                        {{ item.title }}
                    </div>
                </mat-expansion-panel-header>
                <div class="ds-accordion__content">
                    {{ item.content }}
                </div>
            </mat-expansion-panel>
        </mat-accordion>
    `
})
export class QDSAccordionComponent {
    @Input() customClasses: string = '';
    @Input() openSingleItem: boolean = false;
    @Input() defaultExpanded: boolean = false;
    @Input() accordionContent: {
        title: string;
        content: string;
        icon: string;
    }[] = [];
}

import { Component, EventEmitter, Input, Output } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';
import { MatAutocompleteModule } from '@angular/material/autocomplete';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { FormControl } from '@angular/forms';
import { Observable, startWith, map } from 'rxjs';

@Component({
    selector: 'qds-autocomplete',
    standalone: true,
    imports: [
        CommonModule,
        FormsModule,
        ReactiveFormsModule,
        MatAutocompleteModule,
        MatFormFieldModule,
        MatInputModule
    ],
    template: `
        <mat-form-field
            class="ds-input"
            [class]="customClasses"
            [class.--error]="
                formControlId && formControlId.invalid && formControlId.touched
            "
            [class.--required]="isRequired"
            [ngStyle]="{ 'max-width.px': maxWidth ? maxWidth : null }"
        >
            <mat-label *ngIf="label" class="ds-input__label">
                <span>{{ label }}</span>
            </mat-label>
            <span *ngIf="hasIcon" class="ds-icon--search" matPrefix></span>
            <input
                type="text"
                matInput
                [matAutocomplete]="auto"
                [formControl]="formControlId"
                [id]="inputId"
                [placeholder]="placeholder"
                [required]="isRequired"
            />
            <mat-autocomplete
                class="ds-dropdown"
                [class]="panelClasses"
                #auto="matAutocomplete"
                (optionSelected)="onSelectionChange($event.option.value)"
            >
                <mat-option
                    *ngFor="
                        let option of filteredOptions | async;
                        trackBy: trackByOption
                    "
                    class="ds-dropdown__item"
                    [value]="option"
                >
                    {{ option }}
                </mat-option>
            </mat-autocomplete>

            <div *ngIf="hintMessage" class="ds-input__hint">
                {{ hintMessage }}
            </div>

            <div
                *ngIf="
                    formControlId &&
                    formControlId.invalid &&
                    formControlId.touched
                "
                class="ds-input__error"
            >
                {{ errorMessage }}
            </div>
        </mat-form-field>
    `
})
export class QDSAutocompleteComponent {
    @Input() customClasses: string = '';
    @Input() formControlId: FormControl = new FormControl();
    @Input() errorMessage: string = '';
    @Input() hintMessage: string = '';
    @Input() hasIcon: boolean = false;
    @Input() inputId: string = '';
    @Input() label: string = '';
    @Input() panelClasses: string = '';
    @Input() placeholder: string = '';
    @Input() isRequired: boolean = false;
    @Input() maxWidth: number = 0;
    @Input() options: any[] = [];

    @Output() getSelection = new EventEmitter<any>();

    filteredOptions: Observable<string[]> | undefined;

    trackByOption(index: number, option: string): string {
        return option;
    }

    onSelectionChange(selection: any) {
        this.getSelection.emit(selection);
    }

    ngOnInit() {
        this.filteredOptions = this.formControlId.valueChanges.pipe(
            startWith(''),
            map(value => this._filter(value || ''))
        );
    }
    private _filter(value: string): string[] {
        const filterValue = value.toLowerCase();
        return this.options.filter(option =>
            option.toLowerCase().includes(filterValue)
        );
    }
}

import {
    AfterViewInit,
    Component,
    ElementRef,
    EventEmitter,
    Input,
    Output,
    Renderer2
} from '@angular/core';

@Component({
    selector: 'qds-avatar',
    template: `
        <ng-container *ngIf="isButton; else divTemplate">
            <button
                class="ds-avatar"
                [ngClass]="getSize()"
                [class]="customClasses"
                [class.--disabled]="isDisabled"
                [class.--notification]="hasNotification"
                [class.--solid]="!isOutlined"
                [class.--outlined]="isOutlined"
                (click)="onClick($event)"
            >
                {{ initial }}
            </button>
        </ng-container>
        <ng-template #divTemplate>
            <div
                class="ds-avatar"
                [ngClass]="getSize()"
                [class]="customClasses"
                [class.--disabled]="isDisabled"
                [class.--notification]="hasNotification"
                [class.--solid]="!isOutlined"
                [class.--outlined]="isOutlined"
            >
                {{ initial }}
            </div>
        </ng-template>
    `
})
export class QDSAvatarComponent implements AfterViewInit {
    @Input() customClasses: string = '';
    @Input() isButton: boolean = false;
    @Input() isDisabled: boolean = false;
    @Input() isOutlined: boolean = false;
    @Input() hasNotification: boolean = false;
    @Input() initial: string = '';
    @Input() size: 'sm' | 'md' | 'lg' | 'xl' = 'lg';

    @Output() clickHandler = new EventEmitter<Event>();

    onClick(event: Event) {
        this.clickHandler.emit(event);
    }

    getSize() {
        return `--${this.size}`;
    }

    constructor(private el: ElementRef, private renderer: Renderer2) {}

    ngAfterViewInit() {
        const attrs = this.el.nativeElement.getAttributeNames();
        attrs.forEach((attr: string) =>
            this.renderer.removeAttribute(this.el.nativeElement, attr)
        );
    }
}

import {
    AfterViewInit,
    Component,
    ElementRef,
    Input,
    Renderer2
} from '@angular/core';

@Component({
    selector: 'qds-badge',
    template: `
        <div
            class="ds-badge"
            [class]="customClasses"
            [ngClass]="getColor()"
            [ngClass]="getStatus()"
            [class.--circle]="isCircle"
            [class.--secondary]="secondary"
            [class.--dot]="hasNotification"
            [class.--neutral]="!status"
            role="status"
        >
            {{ label }}
        </div>
    `
})
export class QDSBadgeComponent implements AfterViewInit {
    @Input() color: string = '';
    @Input() customClasses: string = '';
