    }

    constructor(
        public toastRef: MatSnackBarRef<QDSToastComponent>,
        private renderer: Renderer2,
        @Inject(MAT_SNACK_BAR_DATA) public data: any
    ) {
        this.hideDismiss = data.hideDismiss;
        this.hideIcon = data.hideIcon;
        this.message = data.message;
        this.positionYOffset = data.positionYOffset;
        this.positionYOffsetMobile = data.positionYOffsetMobile;
        this.textLinkLabel = data.textLinkLabel;
    }

    ngAfterViewInit(): void {
        this.updateOffset();
    }

    @HostListener('window:resize', ['$event'])
    onResize() {
        this.updateOffset();
    }

    private updateOffset(): void {
        const snackBarContainer = document.querySelector(
            '.mat-mdc-snack-bar-container'
        );
        if (snackBarContainer) {
            let offset: string;

            if (window.innerWidth < 900) {
                offset = this.positionYOffsetMobile
                    ? `${this.positionYOffsetMobile}px`
                    : '40px';
            } else {
                offset = this.positionYOffset
                    ? `${this.positionYOffset}px`
                    : '64px';
            }

            if (
                this.toastRef.containerInstance.snackBarConfig
                    .verticalPosition === 'bottom'
            ) {
                this.renderer.removeStyle(snackBarContainer, 'margin-top');
                this.renderer.setStyle(
                    snackBarContainer,
                    'margin-bottom',
                    offset
                );
            } else {
                this.renderer.removeStyle(snackBarContainer, 'margin-bottom');
                this.renderer.setStyle(snackBarContainer, 'margin-top', offset);
            }
        }
    }
}

import {
    AfterViewInit,
    Component,
    ElementRef,
    Input,
    Renderer2
} from '@angular/core';

@Component({
    selector: 'qds-toggle',
    template: `
        <mat-slide-toggle
            class="ds-toggle-switch"
            [class]="customClasses"
            [class.--disabled]="isDisabled"
            [id]="inputId"
            [labelPosition]="labelPosition"
            [attr.aria-label]="label"
            [checked]="isChecked"
        >
            {{ label }}
        </mat-slide-toggle>
    `
})
export class QDSToggleComponent implements AfterViewInit {
    @Input() customClasses: string = '';
    @Input() inputId: string = '';
    @Input() isChecked: boolean = false;
    @Input() isDisabled: boolean = false;
    @Input() label: string = '';
    @Input() labelPosition: 'before' | 'after' = 'after';

    constructor(private el: ElementRef, private renderer: Renderer2) {}

    ngAfterViewInit() {
        const attrs = this.el.nativeElement.getAttributeNames();
        attrs.forEach((attr: string) =>
            this.renderer.removeAttribute(this.el.nativeElement, attr)
        );
    }
}

import {
    AfterViewInit,
    Component,
    ElementRef,
    Input,
    Renderer2,
    ViewChild
} from '@angular/core';
import { MatTooltip } from '@angular/material/tooltip';

@Component({
    selector: 'qds-tooltip',
    template: `
        <span
            #tooltip="matTooltip"
            matTooltip="{{ message }}"
            [matTooltipPosition]="position"
        >
            <ng-content #trigger></ng-content>
        </span>
    `
})
export class QDSTooltipComponent implements AfterViewInit {
    @Input() message: string = '';
    @Input() position: 'above' | 'below' | 'left' | 'right' = 'above';

    @ViewChild('tooltip', { static: true }) tooltip!: MatTooltip;
    @ViewChild('trigger', { static: true }) trigger!: ElementRef;

    constructor(private el: ElementRef, private renderer: Renderer2) {}

    ngAfterViewInit() {
        const attrs = this.el.nativeElement.getAttributeNames();
        attrs.forEach((attr: string) =>
            this.renderer.removeAttribute(this.el.nativeElement, attr)
        );
    }
}

